#!/bin/bash
echo -e "\e[31mWelcome to the PhotoFind backend API testing script!!\n\e[39mFor some tests you will be required to copy and paste the return values of previous scripts."

read -p $'\n\n\e[32mTest 0: \e[39mCreate a user via anonymous backdoor Admin function - Expected response is the ID of the created user. \nPress enter to run this test...'
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded" -d 'email=me1@here.com&password=password123&firstName=AdminTest&username=admintest1&country=Canada&city=Toronto&isAdmin=1' "http://localhost:3000/api/users/signup"
echo -e "\n\e[32mTest 0 Complete.\e[39m"
echo -e "\n\e[93mYou will need the above 'userID' for future tests. Please copy the ID from above into your notepad now.\e[39m"

#WE SHOULD LOGIN here

#UPDATE OTHER TESTS TO REQUIRE LOGIN

read -p $'\n\n\e[32mTest 1: \e[39mCreate a new game - Expected response is the ID of the created game. \nPlease provide the userID created in Test 0:' variable
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded" -d "gameCountry=Canada&gameName=PhotoFind Test Game&userID=58dd74b25e2bfc25a8a6fa94&gameDifficulty=3&gameInfo=This is a test game for the PhotoFind web application.&gamePhoto=data:image/jpeg;base64,&gameCity=Toronto&active=true" "http://localhost:3000/api/games/create"
echo -e "\n\e[32mTest 1 Complete.\e[39m"
echo -e "\n\e[93mYou will need the above 'gameID' for future tests. Please copy the ID from above into your notepad now.\e[39m"

read -p $'\n\n\e[32mTest 2: \e[39mGet game details - Expected response is all values from the game you just created in Test 1. \nPlease provide the gameID created in Test 1:' variable
curl -v -X GET -H "Content-Type: application/x-www-form-urlencoded"  "http://localhost:3000/api/games/$variable"
echo -e "\n\e[32mTest 2 Complete.\e[39m"

read -p $'\n\n\e[32mTest 3: \e[39mEdit a game - Expected response is nothing (just HTTP 200-OK). \nPlease provide the gameID created in Test 1:' variable
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded"  -d 'gameCountry=Canada&gameName=PhotoFind Test Game&userID=12234&gameDifficulty=3&gameInfo=This is a test game for the PhotoFind web application.&gamePhoto=Base64encodedblahblahblah234lkjsdfljksdfknlblahBase64String&gameCity=Markham&active=true' "http://localhost:3000/api/games/$variable/edit"
echo -e "\n\e[32mTest 3 Complete.\e[39m"

echo -e "\n\nGood Job! You just updated the game created in Test 1.  Note the gameCity returned in Test 2 was Toronto, lets re-run the 'Get game details test' again so you can see it has been updated to Markham."
read -p $'\n\n\e[32mTest 4: \e[39mGet game details to verify Test 3 edit success - Expected response is all values from the game you created in Test 1 with the gameCity now set to Markham. \nOnce again, Please provide the gameID created in Test 1:' variable
curl -v -X GET -H "Content-Type: application/x-www-form-urlencoded"  "http://localhost:3000/api/games/$variable"
echo -e "\n\e[32mTest 4 Complete.\e[39m"

read -p $'\n\n\e[32mTest 5: \e[39mCreate a checkpoint - PhotoFind games contain checkpoints. Expected response is the ID of the checkpoint you create.  \nPlease provide the gameID created in Test 1:' variable
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded"  -d 'checkpointImg=data:image/jpeg;base64,&checkpointHint=super secret hint. dont tell anyone. you just lost points bro.' "http://localhost:3000/api/games/$variable/createCheckpoint"
echo -e "\n\e[32mTest 5 Complete.\e[39m"
echo -e "\n\e[93mYou will need the above 'checkpointID' for future tests. Please copy the ID from above into your notepad now.\e[39m"

read -p $'\n\n\e[32mTest 6: \e[39mGet game checkpoints - Expected result is the checkpoints in the game you created in Test 1. \nPlease provide the gameID created in Test 1:' variable
curl -v -X GET "http://localhost:3000/api/games/$variable/checkpoints"
echo -e "\n\e[32mTest 6 Complete.\e[39m"

read -p $'\n\n\e[32mTest 7: \e[39mCreate another checkpoint - Lets create another checkpoint in your game. \nPlease provide the gameID created in Test 1:' variable
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded"  -d 'checkpointImg=data:image/jpeg;base64,&checkpointHint=This is the hint. hope it helps!' "http://localhost:3000/api/games/$variable/createCheckpoint"
echo -e "\n\e[32mTest 7 Complete.\e[39m"

read -p $'\n\n\e[32mTest 8: \e[39mGet game checkpoints - Expected result is the checkpoints in the game you created in Test 1 (should have more than one now). \nPlease provide the gameID created in Test 1:' variable
curl -v -X GET "http://localhost:3000/api/games/$variable/checkpoints"
echo -e "\n\e[32mTest 8 Complete.\e[39m"

read -p $'\n\n\e[32mTest 9: \e[39mCreate a badge - PhotoFind users can earn badges, let us add one now.  Expected response is the badgeID you create. \nPress enter to run this test...'
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded"  -d 'description=Awesome Prof or TA badge&badgePhoto=Base64asldkfalsdkfjalsdkjflkasjflk' "http://localhost:3000/api/badges/create"
echo -e "\n\e[32mTest 9 Complete.\e[39m"
echo -e "\n\e[93mYou will need the above 'badgeID' for future tests. Please copy the ID from above into your notepad now.\e[39m"

read -p $'\n\n\e[32mTest 10: \e[39mRegister for a game - PhotoFind users can register to play games. You will now register to play the game you created in Test 1.  Expected response is nothing (HTTP 200-OK).  \nPlease provide the gameID created in Test 1:' variable
read -p $'\e[39mPlease provide the userID created in Test 0 to register that user in the game:' variable2
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded"  -d "userID=$variable2" "http://localhost:3000/api/games/$variable"
echo -e "\n\e[32mTest 10 Complete.\e[39m"


read -p $'\n\n\e[32mTest 11: \e[39mGet games your user is playing - Expected result is the games you are registered for playing. \nPlease provide the userID created in Test 0:' variable
curl -v -H "Content-Type: application/json" -X GET "http://localhost:3000/api/users/$variable/gamesplaying"
echo -e "\n\e[32mTest 11 Complete.\e[39m"

echo -e "\nWow! Has anyone told you today that you're doing a fantastic job?  Lets award you with an 'Awesome Prof or TA badge'!"

read -p $'\n\n\e[32mTest 12: \e[39mAward badge - Expected result is the playerBadgeID. \nPlease provide the userID created in Test 0:' variable
read -p $'\e[39mPlease provide the badgeID created in Test 9 to award the user with that badge:' variable2
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded" -d "badgeID=$variable2" "http://localhost:3000/api/admin/grantBadge/$variable"
echo -e "\n\e[32mTest 12 Complete.\e[39m"

read -p $'\n\n\e[32mTest 13: \e[39mGet Users Badges- Expected result is the a list of all your badge IDs. \nPlease provide the userID created in Test 0:' variable
curl -v -H "Content-Type: application/json" -X GET "http://localhost:3000/api/users/$variable/badges"
echo -e "\n\e[32mTest 13 Complete.\e[39m"

read -p $'\n\n\e[32mTest 14: \e[39mGet the scoreboard - Expected result is the sorted scoreboard.\nPress enter to run this test...'
curl -v -H "Content-Type: application/json" -X GET "http://localhost:3000/api/users/scoreboard"

echo -e "\n\e[32mTest 14 Complete.\e[39m"

read -p $'\n\n\e[32mTest 15: \e[39mGet users point history - Expected result is the list of entries in your users points history. \nPlease provide the userID created in Test 0:' variable
curl -v -H "Content-Type: application/json" -X GET "http://localhost:3000/api/users/$variable/pointhistory"
echo -e "\n\e[32mTest 15 Complete.\e[39m"

read -p $'\n\n\e[32mTest 16: \e[39mGet all the games you own/have created - Expected result is the list of games you have created. \nPlease provide the userID created in Test 0:' variable
curl -v -H "Content-Type: application/json" -X GET "http://localhost:3000/api/users/$variable/gameshosting"
echo -e "\n\e[32mTest 16 Complete.\e[39m"

#checkpoint tests
read -p $'\n\n\e[32mTest 17: \e[39mSubmit a photo for a checkpoint - Expected result is the checkpoint submissionID. \nPlease provide the userID created in Test 0:' variable
read -p $'\e[39mPlease provide the checkpointID created in Test 5:' variable2
curl -v -X "POST" "http://localhost:3000/api/checkpoint/$variable2/submissions" -H "Content-Type: application/x-www-form-urlencoded"  -d "submissionPhoto=Base64asdlfjkasdflkjasfdlkjaslfkjsdlfkj&userID=$variable"
echo -e "\n\e[32mTest 17 Complete.\e[39m"
echo -e "\n\e[93mYou will need the above 'submissionID' for future tests. Please copy the ID from above into your notepad now.\e[39m"

read -p $'\n\n\e[32mTest 18: \e[39mGet the list of submissions to a checkpoint - Expected result is the submissionID fields for the checkpoint. \nPlease provide the checkpointID created in Test 5:' variable
curl -v "http://localhost:3000/api/checkpoint/$variable/submissions"
echo -e "\n\e[32mTest 18 Complete.\e[39m"

read -p $'\n\n\e[32mTest 19: \e[39mApprove a checkpoint submission - Expected result is nothing (HTTO 200-OK). \nPlease provide the submissionID created in Test 17:' variable
read -p $'\e[39mPlease provide the gameID created in Test 1:' variable2
read -p $'\e[39mPlease provide the userID created in Test 0 to register that user in the game:' variable3
read -p $'\e[39mPlease provide the checkpointID created in Test 5:' variable4
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded" -d "checkpointID=$variable4&userID=$variable3&submissionID=$variable&points=50&gameID=$variable2" "http://localhost:3000/api/checkpoint/submissions/approve"
echo -e "\n\e[32mTest 19 Complete.\e[39m"

read -p $'\n\n\e[32mTest 20: \e[39mSubmit another photo for a checkpoint (so we can reject it) - Expected result is the checkpoint submissionID. \nPlease provide the userID created in Test 0:' variable
read -p $'\e[39mPlease provide the checkpointID created in Test 5:' variable2
curl -v -X "POST" "http://localhost:3000/api/checkpoint/$variable2/submissions" -H "Content-Type: application/x-www-form-urlencoded"  -d "submissionPhoto=Base64asdlfjkasdflkjasfdlkjaslfkjsdlfkj&userID=$variable"
echo -e "\n\e[32mTest 20 Complete.\e[39m"
echo -e "\n\e[93mYou will need the above 'submissionID' for the next test. Please copy the ID from above into your notepad now.\e[39m"

read -p $'\n\n\e[32mTest 21: \e[39mReject a checkpoint submission - Expected result is nothing (HTTO 200-OK). \nPlease provide the submissionID created in Test 20:' variable
read -p $'\e[39mPlease provide the gameID created in Test 1:' variable2
read -p $'\e[39mPlease provide the userID created in Test 0 to register that user in the game:' variable3
read -p $'\e[39mPlease provide the checkpointID created in Test 5:' variable4
curl -v -X POST -H "Content-Type: application/x-www-form-urlencoded" -d "checkpointID=$variable4&userID=$variable3&submissionID=$variable&points=50&gameID=$variable2" "http://localhost:3000/api/checkpoint/submissions/reject"
echo -e "\n\e[32mTest 21 Complete.\e[39m"

